def on_a_pressed():
    global Kast, Ant_kast
    for index in range(10):
        Kast = randint(1, 6)
        list2[Kast - 1] = list2[Kast - 1] + 1
        Ant_kast += 10
    Enere.set_position(30, Math.round(100 - (list2[0] / Ant_kast * 100)))
    Toere.set_position(50, Math.round(100 - (list2[1] / Ant_kast * 100)))
    Treere.set_position(70, Math.round(100 - (list2[2] / Ant_kast * 100)))
    Firere.set_position(90, Math.round(100 - (list2[3] / Ant_kast * 100)))
    Femmere.set_position(110, Math.round(100 - (list2[4] / Ant_kast * 100)))
    Seksere.set_position(130, Math.round(100 - (list2[5] / Ant_kast * 100)))
controller.A.on_event(ControllerButtonEvent.PRESSED, on_a_pressed)

